// Testimonials Carousel
const TestimonialsCarousel = () => {
  const [activeIndex, setActiveIndex] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setActiveIndex((prev) => (prev + 1) % testimonials.length);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div style={{ marginTop: '32px' }}>
      <div style={{ textAlign: 'center', marginBottom: '16px' }}>
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '8px' }}>
          <GoogleIcon />
          <span style={{ fontSize: '14px', color: 'rgba(255,255,255,0.8)', fontWeight: '500' }}>Reviews</span>
        </div>
        <p style={{ fontSize: '12px', color: 'rgba(255,255,255,0.6)', marginTop: '4px' }}>Trusted by thousands of clients</p>
      </div>

      <TestimonialCard testimonial={testimonials[activeIndex]} />

      <div style={{ display: 'flex', justifyContent: 'center', gap: '6px', marginTop: '16px', flexWrap: 'wrap' }}>
        {testimonials.map((_, index) => (
          <button
            key={index}
            onClick={() => setActiveIndex(index)}
            style={{
              height: '8px',
              borderRadius: '4px',
              border: 'none',
              cursor: 'pointer',
              transition: 'all 0.3s',
              width: index === activeIndex ? '20px' : '8px',
              backgroundColor: index === activeIndex ? 'white' : 'rgba(255,255,255,0.4)'
            }}
          />
        ))}
      </div>
    </div>
  );
};

